class ThemeManager{constructor(){this.themeToggle=document.getElementById("theme-toggle"),this.html=document.documentElement,this.init()}init(){const e=localStorage.getItem("theme"),t=window.matchMedia("(prefers-color-scheme: dark)").matches?"dark":"light",n=e||t;this.setTheme(n),this.themeToggle&&this.themeToggle.addEventListener("click",()=>this.toggle())}toggle(){const e=this.html.classList.contains("dark");this.setTheme(e?"light":"dark")}setTheme(e){e==="dark"?this.html.classList.add("dark"):this.html.classList.remove("dark"),localStorage.setItem("theme",e)}}document.addEventListener("DOMContentLoaded",()=>{new ThemeManager})